mapper.select:
select 

from t_belong_dept
where company_code = #{companyCode,jdbcType=CHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and department_code = #{departmentCode,jdbcType=CHAR}

mapper.delete:
delete from t_belong_dept
where company_code = #{companyCode,jdbcType=CHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and department_code = #{departmentCode,jdbcType=CHAR}

mapper.update:
update t_belong_dept
set position_code = #{positionCode,jdbcType=VARCHAR},
  priority_level = #{priorityLevel,jdbcType=CHAR},
  del_flg = #{delFlg,jdbcType=CHAR},
  reg_account = #{regAccount,jdbcType=VARCHAR},
  reg_time = #{regTime,jdbcType=TIMESTAMP},
  upd_account = #{updAccount,jdbcType=VARCHAR},
  upd_time = #{updTime,jdbcType=TIMESTAMP}
where company_code = #{companyCode,jdbcType=CHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and department_code = #{departmentCode,jdbcType=CHAR}
