mapper.select:
select 

,

from m_category_class
where company_code = #{companyCode,jdbcType=CHAR}
  and category_class_number = #{categoryClassNumber,jdbcType=CHAR}

mapper.delete:
delete from m_category_class
where company_code = #{companyCode,jdbcType=CHAR}
  and category_class_number = #{categoryClassNumber,jdbcType=CHAR}

mapper.update:
update m_category_class
set category_class_name = #{categoryClassName,jdbcType=VARCHAR},
  category_system_flg = #{categorySystemFlg,jdbcType=CHAR},
  del_flg = #{delFlg,jdbcType=CHAR},
  reg_account = #{regAccount,jdbcType=VARCHAR},
  reg_time = #{regTime,jdbcType=TIMESTAMP},
  upd_account = #{updAccount,jdbcType=VARCHAR},
  upd_time = #{updTime,jdbcType=TIMESTAMP}
where company_code = #{companyCode,jdbcType=CHAR}
  and category_class_number = #{categoryClassNumber,jdbcType=CHAR}
