mapper.select:
select 

from m_category
where company_code = #{companyCode,jdbcType=CHAR}
  and category_class_number = #{categoryClassNumber,jdbcType=CHAR}
  and category_kbn = #{categoryKbn,jdbcType=CHAR}

mapper.delete:
delete from m_category
where company_code = #{companyCode,jdbcType=CHAR}
  and category_class_number = #{categoryClassNumber,jdbcType=CHAR}
  and category_kbn = #{categoryKbn,jdbcType=CHAR}

mapper.update:
update m_category
set category_kbn_name = #{categoryKbnName,jdbcType=VARCHAR},
  category_kbn_abbreviation = #{categoryKbnAbbreviation,jdbcType=VARCHAR},
  category_kbn_option1 = #{categoryKbnOption1,jdbcType=VARCHAR},
  category_kbn_option2 = #{categoryKbnOption2,jdbcType=VARCHAR},
  del_flg = #{delFlg,jdbcType=CHAR},
  reg_account = #{regAccount,jdbcType=VARCHAR},
  reg_time = #{regTime,jdbcType=TIMESTAMP},
  upd_account = #{updAccount,jdbcType=VARCHAR},
  upd_time = #{updTime,jdbcType=TIMESTAMP}
where company_code = #{companyCode,jdbcType=CHAR}
  and category_class_number = #{categoryClassNumber,jdbcType=CHAR}
  and category_kbn = #{categoryKbn,jdbcType=CHAR}
