mapper.select:
select 

from m_menu
where company_code = #{companyCode,jdbcType=CHAR}
  and menu_number = #{menuNumber,jdbcType=CHAR}
  and permission_kbn = #{permissionKbn,jdbcType=CHAR}

mapper.delete:
delete from m_menu
where company_code = #{companyCode,jdbcType=CHAR}
  and menu_number = #{menuNumber,jdbcType=CHAR}
  and permission_kbn = #{permissionKbn,jdbcType=CHAR}
