mapper.select:
select 

from m_menu_style
where company_code = #{companyCode,jdbcType=CHAR}
  and menu_number = #{menuNumber,jdbcType=CHAR}

mapper.delete:
delete from m_menu_style
where company_code = #{companyCode,jdbcType=CHAR}
  and menu_number = #{menuNumber,jdbcType=CHAR}

mapper.insert:
insert into m_menu_style (company_code, menu_number, menu_button_name, 
  button_color, font_color, font_size, 
  menu_disp_url, menu_icon, registration_user, 
  registration_datetime, update_user, update_datetime
  )
values (#{companyCode,jdbcType=CHAR}, #{menuNumber,jdbcType=CHAR}, #{menuButtonName,jdbcType=VARCHAR}, 
  #{buttonColor,jdbcType=VARCHAR}, #{fontColor,jdbcType=VARCHAR}, #{fontSize,jdbcType=TINYINT}, 
  #{menuDispUrl,jdbcType=VARCHAR}, #{menuIcon,jdbcType=VARCHAR}, #{registrationUser,jdbcType=VARCHAR}, 
  #{registrationDatetime,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=VARCHAR}, #{updateDatetime,jdbcType=TIMESTAMP}
  )
