mapper.select:
select

from t_month_close_history
where company_code = #{companyCode,jdbcType=CHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and close_month = #{targetMonth,jdbcType=CHAR}

mapper.delete:
delete from t_month_close_history
where company_code = #{companyCode,jdbcType=CHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and close_month = #{targetMonth,jdbcType=CHAR}

mapper.update:
update t_month_close_history
set close_day = #{closeDay,jdbcType=CHAR},
  process_id = #{processId,jdbcType=VARCHAR},
  process_result = #{resultKbn,jdbcType=CHAR},
  reg_account = #{regAccount,jdbcType=VARCHAR},
  reg_time = #{regTime,jdbcType=TIMESTAMP},
  upd_account = #{updAccount,jdbcType=VARCHAR},
  upd_time = #{updTime,jdbcType=TIMESTAMP}
where company_code = #{companyCode,jdbcType=CHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and close_month = #{targetMonth,jdbcType=CHAR}
