mapper.select:
select 

from t_paid_holiday_contents_update
where company_code = #{companyCode,jdbcType=CHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and change_date = #{changeDate,jdbcType=CHAR}
  and sub_number = #{subNumber,jdbcType=VARCHAR}
  and date_ym = #{dateYm,jdbcType=CHAR}
  and assignment_year = #{assignmentYear,jdbcType=CHAR}

mapper.delete:
delete from t_paid_holiday_contents_update
