mapper.select:
select 

from t_paid_holiday_correct
where company_code = #{companyCode,jdbcType=CHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and change_date = #{changeDate,jdbcType=CHAR}
  and sub_number = #{subNumber,jdbcType=VARCHAR}

mapper.delete:
delete from t_paid_holiday_correct
where company_code = #{companyCode,jdbcType=CHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and change_date = #{changeDate,jdbcType=CHAR}
  and sub_number = #{subNumber,jdbcType=VARCHAR}

mapper.update:
update t_paid_holiday_correct
set correct_date = #{correctDate,jdbcType=CHAR},
  correct_kbn = #{correctKbn,jdbcType=VARCHAR},
  correct_days = #{correctDays,jdbcType=REAL},
  correct_reason = #{correctReason,jdbcType=VARCHAR},
  correct_remark = #{correctRemark,jdbcType=VARCHAR},
  registration_user = #{registrationUser,jdbcType=VARCHAR},
  registration_datetime = #{registrationDatetime,jdbcType=TIMESTAMP},
  update_user = #{updateUser,jdbcType=VARCHAR},
  update_datetime = #{updateDatetime,jdbcType=TIMESTAMP}
where company_code = #{companyCode,jdbcType=CHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and change_date = #{changeDate,jdbcType=CHAR}
  and sub_number = #{subNumber,jdbcType=VARCHAR}

mapper.select:
select *
from t_paid_holiday_correct
where company_code = #{companyCode,jdbcType=CHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and change_date = #{changeDate,jdbcType=CHAR}

