mapper.select:
select 

from t_resume_detail
where company_code = #{companyCode,jdbcType=CHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and resume_ver_kbn = #{resumeVerKbn,jdbcType=VARCHAR}
  and resume_seq_no = #{resumeSeqNo,jdbcType=CHAR}

mapper.delete:
