mapper.select:
select 

from t_resume
where company_code = #{companyCode,jdbcType=CHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and resume_ver_kbn = #{resumeVerKbn,jdbcType=VARCHAR}

mapper.delete:
delete from t_resume
where company_code = #{companyCode,jdbcType=CHAR}
  and user_id = #{userId,jdbcType=VARCHAR}
  and resume_ver_kbn = #{resumeVerKbn,jdbcType=VARCHAR}

mapper.select:
select
u.company_code
, u.user_id
, m.department_name
, concat(u.first_name, ' ', u.last_name) as userName
, TIMESTAMPDIFF(YEAR, u.birthday, CURDATE()) as age
from
m_user_detail u
left join t_belong_dept d
on u.company_code = d.company_code
and u.user_id = d.user_id
left join m_department m
on m.company_code = d.company_code
and m.department_code = d.department_code
left join t_resume t
on t.company_code = u.company_code
and t.user_id = u.user_id
left join t_resume_detail re
on t.company_code = re.company_code
and t.user_id = re.user_id
and t.resume_ver_kbn = re.resume_ver_kbn
where
u.company_code = #{companyCode,jdbcType=CHAR}

  and tc_os_windows <= #{osKbn,jdbcType=CHAR}


  and tc_os_unix <= #{osKbn,jdbcType=CHAR}


  and tc_os_solaris <= #{osKbn,jdbcType=CHAR}


  and tc_os_hpux <= #{osKbn,jdbcType=CHAR}


  and tc_os_aix <= #{osKbn,jdbcType=CHAR}


  and tc_os_linux <= #{osKbn,jdbcType=CHAR}


  and  tc_os_dos <= #{osKbn,jdbcType=CHAR}


  and  tc_os_mac <= #{osKbn,jdbcType=CHAR}


  and  tc_pg_cobol <= #{pgKbn,jdbcType=CHAR}


  and tc_pg_java <= #{pgKbn,jdbcType=CHAR}


  and tc_pg_jsp <= #{pgKbn,jdbcType=CHAR}


  and tc_pg_c <= #{pgKbn,jdbcType=CHAR}


  and tc_pg_vb <= #{pgKbn,jdbcType=CHAR}


  and tc_pg_vc <= #{pgKbn,jdbcType=CHAR}


  and tc_pg_sql <= #{pgKbn,jdbcType=CHAR}


  and tc_pg_net <= #{pgKbn,jdbcType=CHAR}


  and tc_pg_perl <= #{pgKbn,jdbcType=CHAR}


  and tc_pg_xml <= #{pgKbn,jdbcType=CHAR}


  and  tc_db_oracle <= #{dbKbn,jdbcType=CHAR}


  and tc_db_hirdb <= #{dbKbn,jdbcType=CHAR}


  and tc_db_sqlserver <= #{dbKbn,jdbcType=CHAR}


  and tc_db_db2 <= #{dbKbn,jdbcType=CHAR}


  and tc_db_mysql <= #{dbKbn,jdbcType=CHAR}


  and  tc_db_postgresql <= #{dbKbn,jdbcType=CHAR}


  and tc_db_access <= #{dbKbn,jdbcType=CHAR}


  and  tc_etc_weblogic <= #{etcKbn,jdbcType=CHAR}


  and  tc_etc_j2ee <= #{etcKbn,jdbcType=CHAR}


  and tc_etc_websphere <= #{etcKbn,jdbcType=CHAR}


  and  tc_etc_tomcat <= #{etcKbn,jdbcType=CHAR}


  and  tc_etc_jp1 <= #{etcKbn,jdbcType=CHAR}


  and  tc_etc_cosminexus <= #{etcKbn,jdbcType=CHAR}


  and  tc_etc_xamarin <= #{etcKbn,jdbcType=CHAR}


  and  tc_etc_xamarin <= #{etcKbn,jdbcType=CHAR}


  and  requirements_flg = #{requirementsFlg,jdbcType=CHAR}


  and  basic_flg = #{basicFlg,jdbcType=CHAR}


  and  specific_flg = #{specificFlg,jdbcType=CHAR}


  and  coding_flg = #{codingFlg,jdbcType=CHAR}


  and  unit_test_flg = #{unitTestFlg,jdbcType=CHAR}


  and  interface_test_flg = #{interfaceTestFlg,jdbcType=CHAR}


  and  system_test_flg = #{systemTestFlg,jdbcType=CHAR}


  and TIMESTAMPDIFF(YEAR, u.birthday, CURDATE()) <= #{age,jdbcType=VARCHAR}


  and  m.department_code = #{departmentCode,jdbcType=CHAR}


  and  experience_years >= #{experienceYears,jdbcType=CHAR}


  and  t.responsible = #{responsible,jdbcType=CHAR}


  and  (u.user_id like CONCAT('%', #{userIdName,jdbcType=CHAR} ,'%')
  or  u.first_name like CONCAT('%', #{userIdName,jdbcType=VARCHAR} ,'%')
  or  u.last_name like CONCAT('%', #{userIdName,jdbcType=VARCHAR} ,'%'))

group by
u.company_code
, u.user_id
, m.department_name
, userName
, age

select
  t.resume_ver_kbn
from
  t_resume t
where t.company_code = #{companyCode,jdbcType=CHAR}
  and t.user_id = #{userId,jdbcType=VARCHAR}

select u.company_code , u.user_id , m.department_name , concat(u.first_name, ' ', u.last_name) as userName , TIMESTAMPDIFF(YEAR, u.birthday, CURDATE()) as age from m_user_detail u left join t_belong_dept d on u.company_code = d.company_code and u.user_id = d.user_id left join m_department m on m.company_code = d.company_code and m.department_code = d.department_code left join t_resume t on t.company_code = u.company_code and t.user_id != u.user_id where u.company_code = #{companyCode,jdbcType=CHAR}
and TIMESTAMPDIFF(YEAR, u.birthday, CURDATE()) <= #{age,jdbcType=VARCHAR}
and m.department_code = #{departmentCode,jdbcType=CHAR}
and u.user_id not in ( select t.user_id from t_resume t group by t.user_id ) group by u.company_code , u.user_id , m.department_name , userName , age
